function formatDateWithTimeZone(d) {
    var strOffset = "";
    var offsetHour = d.getTimezoneOffset() / 60;
    var str = Math.abs(offsetHour).toString().padStart(2, 0);
    if (offsetHour < 0) {
      strOffset = "+" + str + ":00";
    }
    else {
      strOffset = "-" + str + ":00";
    }

    function pad(n){return n<10 ? '0'+n : n}
    return d.getFullYear()+'-'
    + pad(d.getMonth()+1)+'-'
    + pad(d.getDate())+'T'
    + pad(d.getHours())+':'
    + pad(d.getMinutes())+':'
    + pad(d.getSeconds()) + strOffset
}

